load plttrim11.matsweepinsim('aeroplt');formdataCXtrim = CX;CYtrim = CY;CZtrim = CZ;CLtrim = CL_;CDtrim = CD_;elevator_avg = -10;sim('aeroplt');formdataCXm10 = CX;CYm10 = CY;CZm10 = CZ;CLm10 = CL_;CDm10 = CD_;figuresurf(bplt,aplt,CLtrim')set(gca,'fontsize',12)xlabel('beta, deg')ylabel('alpha, deg')zlabel('CL')title('CL at Trim')view(-37.5,30)figuresurf(bplt,aplt,CDtrim')set(gca,'fontsize',12)xlabel('beta, deg')ylabel('alpha, deg')zlabel('CD')title('CD at Trim')view(180-37.5,30)figuresurf(bplt,aplt,CXtrim')set(gca,'fontsize',12)xlabel('beta, deg')ylabel('alpha, deg')zlabel('XB')title('XB at Trim')view(180-37.5,30)figuresurf(bplt,aplt,CYtrim')set(gca,'fontsize',12)xlabel('beta, deg')ylabel('alpha, deg')zlabel('YB')title('YB at Trim')view(180-37.5,30)figuresurf(bplt,aplt,CZtrim')set(gca,'fontsize',12)xlabel('beta, deg')ylabel('alpha, deg')zlabel('ZB')title('ZB at Trim')view(180-37.5,30)figuresurf(bplt,aplt,CXm10'-CXtrim')set(gca,'fontsize',12)xlabel('beta, deg')ylabel('alpha, deg')zlabel('XB-10 - XBtrim')title('XB Difference')view(180-37.5,30)figuresurf(bplt,aplt,CYm10'-CYtrim')set(gca,'fontsize',12)xlabel('beta, deg')ylabel('alpha, deg')zlabel('YB-10 - YBtrim')title('YB Difference')view(180-37.5,30)figuresurf(bplt,aplt,CZm10'-CZtrim')set(gca,'fontsize',12)xlabel('beta, deg')ylabel('alpha, deg')zlabel('ZB-10 - ZBtrim')title('ZB Difference')view(180-37.5,30)